#read from a file
def read_coefficients(filename):
    coefficients = {}
    try:
        with open(filename, 'r') as file:
            for line in file:
                if '=' in line:
                    key, value = line.strip().split('=')
                    coefficients[key.strip()] = float(value.strip())
    except FileNotFoundError:
        print(f"Error: File '{filename}' not found.")
        return None
    return coefficients
filename = 'coefficients.txt'

coeffs = read_coefficients(filename)

if coeffs:
    a = coeffs.get('a', 0)
    b = coeffs.get('b', 0)
    c = coeffs.get('c', 0)

    print("Hour\tTemperature (°C)")
    for t in range(0, 25):
        temp = a * t**2 + b * t + c
        print(f"{t}\t{temp:.2f}")
